import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';  // Usar en caso de React Router v6
import '../styles/PostList.css';

function PostList() {
  const [posts, setPosts] = useState([]);
  const navigate = useNavigate();  // Usar en caso de React Router v6

  useEffect(() => {
    fetch('https://dummyapi.online/api/blogposts')
      .then(response => response.json())
      .then(data => setPosts(data))
      .catch(error => console.error('There was a problem with the fetch operation:', error));
  }, []);

  const handleReadMore = (postId) => {
    // Navega a la página de detalles del post
    navigate(`/post/${postId}`);  // Usar en caso de React Router v6
  };

  return (
    <div className="post-list">
      {posts.map(post => (
        <div key={post.id} className="post-item">
          <h2 className="post-title">{post.title}</h2>
          <p className="post-author">by {post.author}</p>

          {/* Si la API devuelve directamente la URL de la imagen, úsala en lugar de construirla manualmente */}
          <img
            className="post-image"
            src={post.image_url ? post.image_url : `https://dummyapi.online/api/image/${post.id}`} 
            alt={post.title}
          />

          <button
            className="read-more-button"
            onClick={() => handleReadMore(post.id)}
          >
            Read More
          </button>
        </div>
      ))}
    </div>
  );
}

export default PostList;
