import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/PostDetails.css';  // Importa los estilos personalizados

function PostDetails() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`https://dummyapi.online/api/blogposts/${id}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Error al obtener los detalles del post');
        }
        return response.json();
      })
      .then(data => setPost(data))
      .catch(error => setError(error.message));
  }, [id]);

  const handleBackToPosts = () => {
    navigate('/');
  };

  if (error) {
    return <div className="error-message">Error: {error}</div>;
  }

  if (!post) {
    return <div className="loading-message">Cargando...</div>;
  }

  return (
    <div className="post-details-container">
      <button className="back-button" onClick={handleBackToPosts}>
        &larr; Volver al listado
      </button>
      <div className="post-content">
        <h1 className="post-title">{post.title}</h1>
        <p className="post-author"><strong>Autor:</strong> {post.author}</p>
        <p className="post-date"><strong>Publicado en:</strong> {post.date_published}</p>
        <div className="post-body">
          {post.content}
        </div>
      </div>
    </div>
  );
}

export default PostDetails;
