import React, { useState, useEffect } from 'react';
import { useParams, useNavigate } from 'react-router-dom';
import '../styles/PostDetails.css';

function PostDetails() {
  const { id } = useParams();
  const [post, setPost] = useState(null);
  const [error, setError] = useState(null);
  const navigate = useNavigate();

  useEffect(() => {
    fetch(`https://dummyapi.online/api/blogposts/${id}`)
      .then(response => {
        if (!response.ok) {
          throw new Error('Error al obtener los detalles del post');
        }
        return response.json();
      })
      .then(data => setPost(data))
      .catch(error => setError(error.message));
  }, [id]);

  if (error) {
    return <div className="error-message">Error: {error}</div>;
  }

  if (!post) {
    return <div className="loading-message">Cargando...</div>;
  }

  return (
    <div className="post-details-container">
      <button className="back-button" onClick={() => navigate('/posts')}>
        Volver al listado
      </button>
      <h1 className="post-title">{post.title}</h1>
      <div className="post-body">
        {post.content}
      </div>
      <div className="post-meta">
        <p className="post-author">Autor: {post.author}</p>
        <p className="post-date">Publicado en: {post.date_published}</p>
      </div>
    </div>
  );
}

export default PostDetails;
